/**
 * Register upgrades with flowManager's upgrade manager
 * @param  {object} apibuilder - APIBuilder instance
 */
function register(apibuilder) {
	const upgrades = require('./upgrades');
	apibuilder.flowManager.registerUpgrades(Object.values(upgrades));
}

/**
 * Unregister upgrades from flowManager's upgrade manager
 * @param  {object} apibuilder - APIBuilder instance
 */
function unregister(apibuilder) {
	const upgrades = require('./upgrades');
	apibuilder.flowManager.unregisterUpgrades(Object.values(upgrades));
}

module.exports = {
	register,
	unregister
};
