const path = require('path');

function swaggersLocation () {
	return path.resolve(process.cwd(), 'swagger');
}

function confFileLocation (serviceName) {
	return path.join(process.cwd(), 'conf', `${serviceName}.default.js`);
}

function getSwagger(fileName) {
	return require(path.join(swaggersLocation(), `${fileName}.json`));
}

/**
 * Get the swagger absolute path.
 *
 * @param {String} fileName  - The swagger's filename.
 * @param {String} extension  - The swagger's extension.
 *
 * @returns {String} - The swagger's absolute path.
 */
function getSwaggerPath(fileName, extension) {
	return path.join(swaggersLocation(), `${fileName}${extension}`);
}

function getIconLocation(fileName) {
	return path.join(swaggersLocation(), fileName);
}

module.exports = { confFileLocation, swaggersLocation, getSwagger, getSwaggerPath, getIconLocation };

