
/**
 * Formats the body as JavaScript.
 * @param req
 * @param resp
 * @param body
 * @param singular
 * @param plural
 * @param callback
 */
exports.format = function javascriptFormatter(req, resp, body, singular, plural, callback) {
	resp && resp.set('Content-Type', 'text/javascript');
	callback(null, JSON.stringify(body));
};

exports.extensions = 'js';
exports.defaultMimeType = 'text/javascript';
