/**
 * Returns the type of credential value for comparison.  It only returns valid types:
 * string, null, or undefined.  If any other type is provided (e.g. Number, Date), the
 * value is as if it is undefined.
 *
 * @param {string|null|undefined} value - The credential value to examine.
 * @returns {string} The typeof credential value, one of: string, null, or undefined
 */
function typeofCredential(value) {
	const tvalue = typeof (value);
	if (tvalue === 'string') {
		return tvalue;
	} else if (value === null) {
		return 'null';
	}
	return 'undefined';
}

exports = module.exports = {
	typeofCredential
};
