class GroupSummary {
	static get schema() {
		return {
			title: 'GroupSummary',
			properties: {
				id: {
					type: 'string'
				},
				name: {
					type: 'string'
				},
				description: {
					type: 'string'
				},
				endpointCount: {
					type: 'number'
				},
				status: {
					$ref: '#/definitions/Status'
				}
			},
			required: [
				'id',
				'name',
				'endpointCount'
			]
		};
	}

	static validate(groupSummary) {
		let valid = true;
		this.schema.required.forEach(prop => {
			if (!{}.hasOwnProperty.call(groupSummary, prop)) {
				valid = false;
			}
		});
		return valid;
	}
}

exports = module.exports = GroupSummary;
