class Status {
	static get schema() {
		return {
			type: 'object',
			description: 'enabled status of the component and whether or not it has any errors',
			required: [ 'enabled' ],
			properties: {
				enabled: {
					type: 'boolean'
				},
				errors: {
					items: {
						type: 'string'
					}
				}
			}
		};
	}
}

exports = module.exports = Status;
