const { CredentialManager } = require('@axway/axway-flow-authorization');

class CredentialsController {
	constructor(ctx) {
		this.ctx = ctx;
	}

	/*
	 * returns an array of managed credentials
	 */
	getCredentials() {
		return Promise.resolve(CredentialManager.getCredentialConfigs());
	}
}

exports = module.exports = CredentialsController;
