/**
 * The metadata for the codeblock.
 * @public
 */
class Codeblock {
	/**
     * Create a Codeblock info.
     *
     * @param {object} codeblock - The values to initialize this Codeblock with.
     */
	constructor(codeblock) {
		this.name = codeblock.name;
		this.description = codeblock.description;
	}
}

exports = module.exports = Codeblock;
