/**
 * The system configuration.
 * @public
 */
class SystemConfig {
	/**
	 * Create a SystemConfig.
	 *
	 * @param {object} config - The values to initialize this SystemConfig with.
	 */
	constructor(config) {
		Object.assign(this, config);
	}

	/**
	 * The swagger schema for a SystemConfig.
	 * @return {object} - The schema for a SystemConfig.
	 */
	static get schema() {
		return {
			title: 'System Configuration',
			type: 'object',
			properties: {
				productName: {
					type: 'string'
				},
				productVersion: {
					type: 'string'
				},
				productRelease: {
					type: 'string'
				},
				apiPrefix: {
					type: 'string'
				}
			}
		};
	}

	/**
	 * Validate that object is a SystemConfig.
	 * @param {SystemConfig|object} config - The object to validate.
	 * @return {boolean} - true if the object is a valid SystemConfig.
	 */
	static validate(config) {
		return !!config;
	}
}

exports = module.exports = SystemConfig;
